import React, { useState } from "react";
import "./App.css";
import { Editor } from "react-draft-wysiwyg";
import { EditorState } from "draft-js";
import phone from "./gallery/image.png";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js'
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

function App() {
  const [editorState, setEditorState] = useState(EditorState.createEmpty());
  const [text, setText] = useState();
  const [phoneEditorState, setPhoneEditorState] = useState();
  const [phoneText, setPhoneText] = useState();

  const onInputChange = (input) => {
    setEditorState(input);
    setPhoneEditorState(input);
    //onPhoneEditorChange(input);
  };
  const onContentStateChange = (content) => {
    // if (content.blocks[0].text) {
    //   setText(content.blocks[0].text);
    //   //onPhoneContentStateChange(content);
    // }
    setText(content);
    setPhoneText(content);
    console.log(phoneText);
  };
  const onPhoneEditorChange = (input) => {
    setPhoneEditorState(input);
  };

  const onPhoneContentStateChange = (content) => {
    if (content.blocks[0].text) {
      setPhoneText(content.blocks[0].text);
      // setPhoneText(content.blocks[0].text);
    }
    return null;
  };
  return (
    <div className="App">
      <div className="row no-gutters">
        <div className="col-6" style="margin-left: 6%; margin-top: 8%;">
          <div className="d-flex flex-column align-items-start">
            <span className="mb-5" style="font-family: sans-serif;font-size: 20px;font-weight: 700;">Customisation</span>

            <Editor
              //initialContentState={text}
              editorState={editorState}
              toolbarClassName="toolbarClassName"
              wrapperClassName="wrapperClassName"
              editorClassName="editorClassName"
              onEditorStateChange={(editor) => onInputChange(editor)}
              // onContentStateChange={(content) => onContentStateChange(content)}
            />

            <button class="py-1 px-3 mt-5" style="background-color: #418DCB; color: white;">
              Publish    &gt; 
            </button>
          </div>
        </div>

        <div className="col-5 d-flex" style="margin-top: 6%; color: white;">
          <img src={phone} alt="Phone" className="phone"/>
          <Editor
            //initialContentState={phoneText}
            editorState={editorState}
            toolbarHidden
            //readOnly
            toolbarClassName="toolbarClassName"
            wrapperClassName="wrapperClassName"
            editorClassName="editorClassName"
            //onEditorStateChange={(editor) => setPhoneEditorState(editor)}
            onEditorStateChange={(editor) => {
              onInputChange(editor);
            }}

            //onContentStateChange={(content) => onContentStateChange(content)}
            // onContentStateChange={(content) => {
            //   setPhoneText(content);
            //   console.log("w");
            // }}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
